import styled from 'styled-components'


export const ArtistContainer = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    background: #e2e2e2;

    @media screen and (max-width: 768px){
        height: 1100px;
    }

    @media screen and (max-width: 480px){
        height: 1300px;
    }
`
export const ArtistInfoWrapper = styled.div`
    display: grid;
    word-break: keep-all;
    margin: 0 auto;
    margin-top: 4.8%; 
    margin-left: 13.3%; 
    text-align: left;

    @media screen and (max-width: 1000px){
        grid-template-columns: 1fr 1fr;
    }

    @media screen and (max-width: 768px){
        grid-template-columns: 1fr;
        padding: 0 20px;
    }

`

export const ArtistH1 = styled.h1`
    color: #000;
    margin-bottom: 42.5px;
    width: 202px;
    height: 67px;
    object-fit: contain;
    font-family: Ubuntu;
    font-size: 60px;
    font-weight: 500;
    font-stretch: normal;
    font-style: normal;
    letter-spacing: -3.6px;
    text-align: left;
    color: #000;

    @media screen and(max-width: 480px){
        font-size: 2rem;
    }
`
export const ArtistH3 = styled.h3`
    width: 532px;
    margin-bottom: 0;
    object-fit: contain;
    font-family: NanumSquareOTFR;
    font-size: 18px;
    font-weight: normal;
    font-stretch: normal;
    font-style: normal;
    line-height: 1.67;
    letter-spacing: -0.72px;
    text-align: left;
    color: #8d8d8d;
`
export const Column = styled.div`
    margin-top: 48px;
`

export const Column2 = styled.div`
    margin: 0 auto;
    margin-top: 8%;
    margin-bottom: 5%;
    display: flex;


`

export const ArtistCardRow1 = styled.div`
  display: flex;
`

export const ArtistInfoCard = styled.div`
    background: #fff;
    display: flex;
    width: 495px;
    height: 204px;
    margin-right : 7%;
    word-break: keep-all;
    justify-content: flex-start;
    align-items: center;
    border-radius: 10px;
    max-height: 340px;
    transition: all 0.2s ease-in-out;

`
export const InfoIcon = styled.img`
    width: 494.5px;
    height: 204px;
    vertical-align: middle;
`
export const InfoNum = styled.h1`
    position: absolute;
    font-size: 54.3px;
    font-family: NanumSquareOTFEB;
    margin: 0 2% 0 3.9%;
` 
export const InfoH2 = styled.h2`
    position: absolute;
    width: 110px;
    height: 22px;
    margin: 0 5% 6% 5%;
    object-fit: contain;
    font-family: NanumSquareOTFEB;
    font-size: 20px;
    line-height: 1.45;
    letter-spacing: -0.8px;
    // text-align: center;
    color: var(--vivid-blue);
`
export const InfoH3 = styled.h3`

`

// export const ArtistInfoCard2 = styled.div`
// `

export const ArtistCardRow2 = styled.div`
    margin-left: 279.5px;
    margin-top: 50px;
    display: flex;

`
export const ArtistInfoCard3 = styled.div`
    margin-right: 32px;

`

export const ArtistInfoCard4 = styled.div`

`


export const ArtistRequired1 = styled.div`

`

export const ArtistRequired2 = styled.div`
`

export const ArtistRequired3 = styled.div`
`
