export const HeroContainer = styled.div`
background: #fff;
background-image: url(${bg});
width: 100%;
height: 1080px;
object-fit: contain;
`

const Slogan = styled.div`
    padding: 12.3% 0px 0 12.9%;
    width: 100%;
`

const VideoBg = styled.video`
    width: 100%;
    height: 100%;
    -o-object-fit: cover;
    object-fit: cover;
    background: #232a34;
`
const SloganH1 = styled.h1`
    width: 680px;
    margin-bottom: 0px;
    font-family: Ubuntu;
    font-size: 110px;
    font-weight: bold;
    line-height: 1.36;
    letter-spacing: -4.4px;
    text-align: left;
    color: var(--black);
`



const HeroSection = () => {
    return (
        <>
            <HeroContainer>
                <Slogan>
                {/* <VideoBg autoPlay loop muted src={Video} type='video/mp4' /> */}
                    <img src={main} alt=""
                        style={{ width: '100%'}}/>
                    {/* <SloganH1>
                        Paint        
