import React from 'react'
import styled from 'styled-components';

export const HeroContainer = styled.div`
background: #2f2d2e;
display: flex;
width: 1920px;
height: 1080px;
justify-content: center;
align-items: center;
padding: 128px 73.9px 604px 0;
object-fit: contain;
`
const SloganH1 = styled.h1`
width: 680px;
  height: 424px;
  font-family: Ubuntu;
  font-size: 110px;
  font-weight: bold;
  font-stretch: normal;
  font-style: normal;
  line-height: 1.36;
  letter-spacing: -4.4px;
  text-align: left;
  color: var(--black);
`

const HeroSection = () => {
    return (
        <>
            <HeroContainer>
                hero
                <SloganH1>
                    Paint
                    Your Own Dot
                    On The World
                </SloganH1>
            </HeroContainer>
        </>
    )
}

export default HeroSection
