import styled from 'styled-components'

export const AboutContainer = styled.div`
    // width: 1920px;
    height: 1000px;
    display: flex;
    flex-direction: column;
    // justify-content: center;
    align-items: center;
    background: #e2e2e2;

    @media screen and (max-width: 768px){
        height: 1100px;
    }

    @media screen and (max-width: 480px){
        height: 1300px;
    }
`
export const AboutInfoWrapper = styled.div`
    // max-width: 1200px;
    margin: 0 auto;
    display: grid;
    marginTop: 173.9px; 
    // marginLeft: 173.9px; 
    textAlign: left;
    // grid-template-columns: 1fr 1fr 1fr;
    align-items: center;
    grid-gap: 16px;
    padding: 0 50px;

    @media screen and (max-width: 1000px){
        grid-template-columns: 1fr 1fr;
    }

    @media screen and (max-width: 768px){
        grid-template-columns: 1fr;
        padding: 0 20px;
    }

`
export const AboutIcon = styled.img`
    height: 160px;
    width: 160px;
    margin-bottom: 10px;
`

export const AboutH1 = styled.h1`
    color: #000;
    margin-bottom: 42.5px;

    width: 202px;
    height: 67px;
    object-fit: contain;
    font-family: Ubuntu;
    font-size: 60px;
    font-weight: 500;
    font-stretch: normal;
    font-style: normal;
    line-height: 1.82;
    letter-spacing: -3.6px;
    text-align: left;
    color: var(--black);

    @media screen and(max-width: 480px){
        font-size: 2rem;
    }
`
export const AboutH3 = styled.h3`
width: 532px;
height: 50px;
object-fit: contain;
font-family: NanumSquareOTFR;
font-size: 18px;
font-weight: normal;
font-stretch: normal;
font-style: normal;
line-height: 1.67;
letter-spacing: -0.72px;
text-align: left;
color: #8d8d8d;

`
export const AboutCardWrapper = styled.div`
    max-width: 1200px;
    margin: 0 auto;
    display: grid;
    grid-template-columns: 1fr 1fr 1fr;
    align-items: center;
    grid-gap: 16px;
    padding: 0 50px;

    @media screen and (max-width: 1000px){
        grid-template-columns: 1fr 1fr;
    }

    @media screen and (max-width: 768px){
        grid-template-columns: 1fr;
        padding: 0 20px;
    }

`
export const AboutCard = styled.img`
width: 457px;
height: 359.6px;
padding: 74.4px 90.7px 84.4px 74.3px;
object-fit: contain;
`

export const AboutH2 = styled.h2`
    font-size: 1rem;
    margin-bottom: 10px;
`

export const AboutP = styled.p`
    font-size: 1rem;
    text-align: center;
`
