import styled from 'styled-components'
import bg from '../../images/section-3-artist.png';


export const ArtistContainer = styled.div`
    display: flex;
    flex-direction: column;
    height: 1300px;
    align-items: center;
    background: #fff;
    background-image: url(${bg});

    @media screen and (max-width: 768px){
        height: 1500px;
    }

    @media screen and (max-width: 480px){
        height: 1800px;
    }
`
export const ArtistInfoWrapper = styled.div`
    display: grid;
    word-break: keep-all;
    margin: 0 auto;
    margin-top: 4.8%; 
    margin-left: 13.3%; 
    text-align: left;



`

export const ArtistH1 = styled.h1`
    color: #000;
    margin-bottom: 42.5px;
    width: 202px;
    height: 67px;
    object-fit: contain;
    font-family: Ubuntu;
    font-size: 60px;
    font-weight: 500;
    font-stretch: normal;
    font-style: normal;
    letter-spacing: -3.6px;
    text-align: left;
    color: #000;

    @media screen and(max-width: 480px){
        font-size: 2rem;
    }
`
export const ArtistH3 = styled.h3`
    width: 532px;
    margin-bottom: 0;
    object-fit: contain;
    font-family: NanumSquareOTFR;
    font-size: 18px;
    line-height: 1.67;
    letter-spacing: -0.72px;
    text-align: left;
    color: #8d8d8d;
`
export const Column = styled.div`
    margin-top: 48px;
    word-break: keep-all;
`

export const Column2 = styled.div`
    margin: 0 auto;
    margin-top: 10.8%;
    margin-bottom: 8.75%;
    display: grid;
    grid-template-columns: 1fr 1fr 1fr;

    @media screen and (max-width: 1600px){
        width: 80%;
        grid-template-columns: 1fr 1fr;
    }

    @media screen and (max-width: 768px){
        grid-template-columns: 1fr;
        padding: 0 20px;
    }
`

export const ArtistCardRow1 = styled.div`
  display: flex;
`

export const ArtistInfoCard = styled.div`
    background: #fff;
    display: flex;
    width: 495px;
    height: 204px;
    margin-right : 2.4%;
    word-break: keep-all;
    justify-content: flex-start;
    align-items: center;
    border-radius: 10px;
    max-height: 340px;
    transition: all 0.2s ease-in-out;

`
export const InfoIcon = styled.img`
    width: 494.5px;
    height: 204px;
    vertical-align: middle;
`
export const InfoNum = styled.h1`
    position: absolute;
    font-size: 54.3px;
    font-family: NanumSquareOTFEB;
    margin: 0 0 1.3% 3.9%;
    letter-spacing: -0.8px;
    color: #1127fe;
` 
export const InfoH2 = styled.h2`
    position: absolute;
    width: 110px;
    height: 22px;
    margin: 0 0 2% 8.4%;
    object-fit: contain;
    font-family: NanumSquareOTFEB;
    font-size: 20px;
    line-height: 1.45;
    letter-spacing: -0.8px;
    text-align: center;
    color: #1127fe;
`
export const InfoH3 = styled.h3`
    position: absolute;
    width: 350px;
    height: 18px;
    margin: 3% 0 0 9%;
    object-fit: contain;
    font-family: NanumSquareOTFR;
    font-size: 16px;
    line-height: 1.63;
    letter-spacing: -0.32px;
    text-align: left;
    color: #1127fe;
`


export const ArtistCardRow2 = styled.div`
    margin-left: 279.5px;
    margin-top: 50px;
    display: flex;

`
export const WhoIcon = styled.img`
    width: 475.5px;
    height: 302.5px;
    // vertical-align: middle;
    // border-radius: 150px;
`


export const ArtistRequired = styled.div`
    background: #fff;
    display: flex;
    width: 450.5px;
    // height: 270.5px;
    border-radius: 150px;
    margin-right : 10%;
    word-break: keep-all;
    justify-content: flex-start;
    align-items: center;
    max-height: 400px;
`

export const RequiredH2 = styled.h2`
position: absolute;
width: 262px;
height: 33px;
margin: 0 5% 7.6% 5%;
font-family: NanumSquareOTFEB;
font-size: 24px;
font-weight: bold;
font-stretch: normal;
font-style: normal;
line-height: 1.71;
letter-spacing: -0.96px;
text-align: center;
color: #474747;
`

export const RequiredH3 = styled.h3`
position: absolute;
width: 350px;
height: 70px;
margin: 2% 3% 0 3%;
font-family: S-CoreDream-4;
font-size: 21px;
line-height: 1.95;
letter-spacing: -0.84px;
text-align: left;
color: #6d6d6d;
text-align: center;

`
