import React from 'react'
import styled from 'styled-components';
import bg from '../../images/video.png'

export const HeroContainer = styled.div`
background: #2f2d2e;
background-image: url(${bg});
display: flex;
width: 100%;
height: 1080px;

object-fit: contain;
`

const Slogan = styled.div`
padding: 10% 0px 0 12.9%;
width: 100%;


`
const SloganH1_1 = styled.h1`
    // position: abso;
    width: 680px;
    margin-bottom: 0px;
    font-family: Ubuntu;
    font-size: 110px;
    font-weight: bold;
    line-height: 1.36;
    letter-spacing: -4.4px;
    text-align: left;
    color: var(--black);
`
const SloganH1_2 = styled.h1`
width: 680px;
  font-family: Ubuntu;
  font-size: 110px;
  font-weight: bold;
  line-height: 1.36;
  letter-spacing: -4.4px;
  text-align: left;
  color: var(--black);
`


const HeroSection = () => {
    return (
        <>
            <HeroContainer>
                <Slogan>
                    {/* <img src={main} alt=""
                        style={{ width: '100%'}}/> */}
                    <SloganH1_1>
                        Paint        
                    </SloganH1_1>
                    <SloganH1_1>
                        Your Own Dot
                        On The World
                    </SloganH1_1>
                </Slogan>
            </HeroContainer>
        </>
    )
}

export default HeroSection
