import styled from 'styled-components';

export const Nav = styled.nav`
    background: #e2e2e2;
    height: 80px;
    display: flex;
    // margin-top: -80px;
    justify-content: center;
    align-items: center;
    font-size: 1rem;
    position: sticky;
    top: 0;
    z-index: 10;

`

export const NavbarContainer = styled.div`
    display: flex;
    justify-content: space-between;
    height: 80px;
    z-index: 1;
    width: 100%;
    padding: 0 24px;
    max-width: 1100px;
`

export const Logo = styled.div`
    // background-image: url('../../image/bdot_logo.png');
    background-image: url('https://i.pinimg.com/236x/05/52/37/055237cde9e33fc7b4e4146289731648.jpg');
    color: #fff;
    justify-self: flex-start;
    cursor: pointer;
    font-size: 1.5rem;
    display: flex;
    align-items: center;
    margin-left: 24px;
    font-weight: bold;
    text-decoration: none;

`

export const NavMenu = styled.ul`
display: flex;
align-items: center;
list-style: none;
list-style: none;
text-align: center;
margin-right: -22px;
`

export const NavItem = styled.li`
    height: 80px;
`
