import styled from 'styled-components';

export const Nav = styled.nav`
    position: absolute;
    display: flex;
    width: 100%;
    flex-direction: column;
    height: 78.7px;
    z-index:99;
    background: #000;

    margin-top: -80px;
    justify-content: center;
    align-items: center;
    font-size: 1rem;
    position: sticky;
    top: 0;
    z-index: 10;

@media screen and (max-width: 960px){
    transition: 0.8s all ease;
}

`



export const NavbarContainer = styled.div`
display: flex;
justify-content: space-between;
height: 80px;
z-index: 1;
width: 100%;
padding: 0 24px;
max-width: 1100px;
`
export const NavMenu = styled.div`
display: flex;
align-items: center;
list-style: none;
list-style: none;
text-align: center;
margin-right: -22px;

@media screen and (max-width: 768px){
    display: none;
}

`

export const Logo = styled.img`
color: #fff;
justify-self: flex-start;
cursor: pointer;
font-size: 1.5rem;
display: flex;
align-items: center;
margin-left: 24px;
font-weight: bold;
text-decoration: none;

`



export const LeftMenu = styled.div`
display: flex;
`

export const HomeLink = styled.a`
color: #fff;
display: flex;
align-items: center;
text-decoration: none;
padding: 0 1rem;
height: 100%;
cursor: pointer;

&.active {
    border-bottom: 3px solid #285DF5;
}

`

export const DotVideoLink = styled.a`
color: #fff;
display: flex;
align-items: center;
text-decoration: none;
padding: 0 1rem;
height: 100%;
cursor: pointer;

&.active {
    border-bottom: 3px solid #285DF5;
}
`

export const FollowLink = styled.a`
color: #fff;
display: flex;
align-items: center;
text-decoration: none;
padding: 0 1rem;
height: 100%;
cursor: pointer;

&.active {
    border-bottom: 3px solid #285DF5;
}
`
export const RightMenu = styled.div`
border-radius: 50px;
background: #285DF5;
color: #fff;
cursor: pointer;
transition: all 0.2s ease-in-out;
text-decoration: none;


`

export const SearchLink = styled.a`
border-radius: 50px;
background: #285DF5;
white-space: nowrap;
padding: 10px 22px;
color: #fff;
font-size: 16px;
outline: none;
border: none;
cursor: pointer;
transition: all 0.2s ease-in-out;
text-decoration: none;

&:hover{
    transition: all 0.2s ease-in-out;
    background: #fff;
    color: #010606;
}
`
